INSERT INTO public.quotations VALUES (2119, 'Quas antequam exponere incipiam, illud imprimis praesumendum est cometas non in una parte caeli aspici nec in signifero tantum orbe sed tam in ortu quam in occasu, frequentissime tamen circa septentrionem.', '', 1879, 'Before I undertake to describe them I must first state that comets are seen not in one part only of the sky or only in the one area of the zodiac but they are seen as much in the east as in the west; most frequently, however, towards the north.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.249.xml', '7.11.1', 2202, '248-249', '');
