INSERT INTO public.quotations VALUES (2028, 'Quinque stellarum potestates Chaldaeorum observatio excepit. Quid, tu tot illa milia siderum iudicas otiosa lucere?', '', 869, 'The observation of the Chaldaeans is restricted to the powers of five planets.3 Well! Do you think that those many thousands of stars shine uselessly?', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume I: Books 1-3. Translated by Thomas H. Corcoran. Loeb Classical Library 450. Cambridge, MA: Harvard University Press, 1971.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL450.153.xml', '2.32.7', 1064, '152-153', '');
INSERT INTO public.quotations VALUES (978, 'sed ad liquidum propter partium minutorumque brevitatem ac velocissimum siderum cursum caelique pronum rotatae vertiginis lapsum neminem pervenire posse confirmant', '', 873, 'But they add that no one can get definite results because of the smallness of the degrees and minutes, the tremendous speed of the stars'' orbits, and the inclination of the heavens.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '1.3.2', 1071, '1.9', '');
