INSERT INTO public.quotations VALUES (2031, '“Quid ergo,” inquit, “non potest aliqua ex his transcurrentibus stellis incidere in nubem et extingui?”', '', 1808, '“Well, then,” he says, “is it not possible that some of those shooting stars fall into a cloud and are extinguished?”', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume I: Books 1-3. Translated by Thomas H. Corcoran. Loeb Classical Library 450. Cambridge, MA: Harvard University Press, 1971.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL450.187.xml', '2.55.3', 2118, '186-187', '');
