INSERT INTO public.quotations VALUES (2296, 'subsequitur rapido contenta Canicula cursu', '', 1476, 'At his heels follows the Dog outstretched in full career:', 'Manilius, Astronomica', 'astrological treatise', '', '', 'Manilius. Astronomica. Edited and translated by G. P. Goold. Loeb Classical Library 469. Cambridge, MA: Harvard University Press, 1977.', 'https://www.loebclassics.com/view/manilius-astronomica/1977/pb_LCL469.35.xml', '1.396', 2368, '34-35', '');
INSERT INTO public.quotations VALUES (2124, 'His illud respondebimus certum esse numerum stellarum mobilium, solere autem eodem tempore et has apparere et cometen, ex quo manifestum fit non illarum coitu fieri cometen sed proprium esse et sui iuris.', '', 1883, 'We will give this answer to their theory: the number of planets is fixed. Moreover, planets are usually visible when a comet also is seen to appear. Consequently it is obvious that a comet is not produced by a conjunction of planets but has its own existence and in its own right.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.251.xml', '7.12.2', 2206, '250-251', '');
