INSERT INTO public.quotations VALUES (2125, 'et Saturnus aliquando supra Iovem est et Mars Venerem aut Mercurium recta linea despicit, nec tamen propter hunc illorum concursum, cum alter alterum subit, cometes fit.', '', 1884, 'Saturn is often above Jupiter, and Mars looks vertically down on Venus or Mercury. And yet no comet is produced as a result of this coming together of planets, when one passes under the other.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.251.xml', '7.12.3', 2207, '250-251', '');
