INSERT INTO public.quotations VALUES (1614, 'Si Luna sic fuerit constituta, ut malivolam stellam in praecedenti signo habeat, aliam in sequenti et sequens Lunam stella Solem quacumque radiatione respiciat, is qui natus fuerit aut exponetur aut statim ut natus fuerit interibit.', '', 1429, 'If the Moon is so located that she has a malefic planet in the sign before hers and another in the sign following, and the one following is in any aspect to the Sun, the native will either be exposed or die at birth.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '7.2.8', 1698, '2.212', '');
INSERT INTO public.quotations VALUES (755, 'Καὶ ἡ μὲν μοῖρα τξ-ον μέρος ἐστὶ τοῦ ζῳδιακοῦ κύκλου, ἡ δὲ ἡμέρα τξε-ον καὶ δ-ον μέρος ὡς ἔγγιστα τοῦ ἐνιαυσιαίου χρόνου.', '', 672, 'Also, the degree is a 360th part of the zodiac, and the day as close as possible to a 365th and a 4th part of the time of a year.', 'Geminus', 'astronomy', '', '1st century BCE', 'Geminus. 1898. Γεμίνου Εἰσαγωγή εἰς τὰ Φαινόμενα, Gemini Elementa Astronomiae, ed. C.H.A. Manitius. Leipzig: B.G. Teubner.', 'http://www.wilbourhall.org/pdfs/GeminusElementaAstronomiae.pdf', '1.8', 866, '4, 6', '');
