INSERT INTO public.quotations VALUES (2565, 'וּנְשָׂאתֶם אֵת סִכּוּת מַלְכְּכֶם וְאֵת כִּיּוּן צַלְמֵיכֶם כּוֹכַב אֱלֹהֵיכֶם אֲשֶׁר עֲשִׂיתֶם לָכֶם׃', 'ûnəśāʾt̲em ʾēt̲ sikkût̲ malkək̲em wəʾēt̲ kiyyûn ṣalmêk̲em kôk̲ab̲ ʾĕlōhêk̲em ʾăšer ʿăśît̲em lāk̲em.', 2238, 'But ye have borne the tabernacle of your Moloch and Chiun your images, the star of your god, which ye made to yourselves.', 'Amos', 'religious text', '', '', 'Stieglitz 1988', '', '5:26', 2633, '445', '"The common Mesopotamian name for this planet was (mul)SAG.UŠ = Kay(y)amānu or Kay(y)ānu."');
