INSERT INTO public.quotations VALUES (1558, 'Locum desideriorum si partiliter volueris invenire, in diurna quidem genitura a parte Daemonis usque ad partem Fortunae conputa', '', 1382, 'If you want to find the lot of longing by degree in a day chart, count from the lot of the spirit until the lot of fortune', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '6.32.45', 1650, '2.187', '');
INSERT INTO public.quotations VALUES (2034, 'Puta enim me confiteri verum esse quod dicis, aliquando post tonitrua emicare ignes stellis transversis et cadentibus similes, non ob hoc tonitrua facta sunt, sed, cum hoc fieret, tonitrua facta sunt.', '', 1810, 'Consider that I admit what you say is true, that sometimes after thunder fires gleam like shooting and falling stars. Thunder was not caused on this account, but when this happened thunder occurred.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume I: Books 1-3. Translated by Thomas H. Corcoran. Loeb Classical Library 450. Cambridge, MA: Harvard University Press, 1971.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL450.187.xml', '2.55.3', 2121, '186-187', '');
INSERT INTO public.quotations VALUES (2127, 'Summa enim velocitas transeuntium est, ideoque omnis defectio siderum brevis est quia cito illa idem cursus qui admoverat abstrahit.', '', 850, 'For, the velocity of stars in transit is very great. Consequently, every eclipse of stars is brief because the same motion that brought them rapidly together separates them rapidly.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.253.xml', '7.12.4', 1042, '252-253', '');
INSERT INTO public.quotations VALUES (2299, 'quae notia antiqui dixerunt sidera vates.', '', 2023, 'these stars the ancient poets have called southern.', 'Manilius, Astronomica', 'astrological treatise', '', '', 'Manilius. Astronomica. Edited and translated by G. P. Goold. Loeb Classical Library 469. Cambridge, MA: Harvard University Press, 1977.', 'https://www.loebclassics.com/view/manilius-astronomica/1977/pb_LCL469.39.xml', '1.446', 2371, '38-39', '');
