INSERT INTO public.quotations VALUES (923, 'DIŠ 30 ina IGI.LA2-šu2 TA U4-1-KAM EN U4-5-KAM | 5 u4-mi U4.SAKAR d.A-ni7 | TA U4-6-KAM EN U4-10-KAM | 5 u4-mi ka-li-tu2 d.E2-a | TA U4-11-KAM EN U4-15-KAM 5 u4-mi AGA taš-ri-iḫ-ti | [ip-pir]-ma d.En-lil2', '', 186, 'The moon at its appearance from the frist day tot he fifth day, | five days, is a crescent, it is Anu; | from the sixth day tot he tenth day, | five days, is a kidney, it is Ea; | from the eleventh day tot he fifteenth day, five days, the crown of splendour | it wears, it is Enlil.', 'K 250+ (CT 26 40-41)', 'star list', 'Nineveh', '7th cent BCE', 'Koch-Westenholz, U., 1995. Mesopotamian Astrology. An Introduction to Babylonian and Assyrian Celestial Divination. Copenhagen: Museum Tusculanum Press.', '', 'vi 16-21', 266, '202', '');
INSERT INTO public.quotations VALUES (1263, 'Διαιρεῖται δὲ ὁ ἐνιαύσιος χρόνος εἰς μέρη δ᾽, ἔαρ θέρος φθινόπωρον χειμῶνα.', '', 1124, 'The time of a year is divided into 4 parts: spring, summer, autumn, winter.', 'Geminus', 'astronomy', '', '1st century BCE', 'Geminus. 1898. Γεμίνου Εἰσαγωγή εἰς τὰ Φαινόμενα, Gemini Elementa Astronomiae, ed. C.H.A. Manitius. Leipzig: B.G. Teubner.', 'http://www.wilbourhall.org/pdfs/GeminusElementaAstronomiae.pdf', '1.9', 1355, '6', '');
