INSERT INTO public.quotations VALUES (1118, 'Est autem ex his residuis quattuor locis primus, qui in secundo ab horoscopo signo constituitur: qui locus Inferna Porta dicitur vel Anafora', '', 957, 'The first of thses remaining four places, however, which is located in the second place from the ascendant, is called the Gate of Hell, or the Anafora (rising up from the Underworld)', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.17.1', 1215, '1.60', '');
