INSERT INTO public.quotations VALUES (225, '(1) ŠU₂ ša₂ KUR a-na ŠU₂ ša₂ KUR (2) zib.ME ḪUN 3.45.20 TAB (3) MUL₂ MAŠ ALLA 3.29.40 TAB (4) A ABSIN 3.28.50 TAB (5) RIN₂ GIR₂.TAB PA 3.38.30 TAB (6) ⸢MAŠ₂ GU⸣ 3.35.10 TAB', '', 234, '(1) Eastern setting to eastern setting: (2) Pisces Aries you add 3,45,20; (3) Taurus Gemini Cancer you add 3,29,40, (4) Leo Virgo you add 3,28,50, (5) Libra Scorpio Sagittarius you add 3,38,30, (6) Capricorn, Aquarius you add 3,35,10.', 'BM 35495 (BMAPT No. 7)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-50 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer.', '', 'rev. vi 1-6', 320, '', '');
