INSERT INTO public.quotations VALUES (717, 'ἄστρα· Ἀττικοὶ τοὺς ἀστέρας', '', 638, 'astra: Athenians; the stars.', 'Hesychius', 'lexicon', 'Alexandria', '4th-6th century CE', 'Hesychius Alexandrinus. 2018. Hesychii Alexandrini Lexicon, Vol. 1 (A - Delta), ed. I.C. Cunningham. Berlin, Boston: Walter de Gruyter.', 'https://doi.org/10.1515/9783110547405', 'α 7883', 836, '360', '');
INSERT INTO public.quotations VALUES (2580, 'αἰδώς· *αἰσχύνη ... τὴν τοῦ σώματος αἰσχρότητα, τὰ αἰδοῖα. καὶ ἡ σελήνη, παρὰ Χαλδαίοις', '', 2252, 'Aidōs: *shame ... the ugliness of the body, the private parts. And the Moon, according to the Chaldeans.', 'Hesychius', 'lexicon', 'Alexandria', '4th-6th century CE', 'Hesychius Alexandrinus. 2018. Hesychii Alexandrini Lexicon, Vol. 1 (A - Delta), ed. I.C. Cunningham. Berlin, Boston: Walter de Gruyter.', 'https://doi.org/10.1515/9783110547405', 'α 1799', 2648, '85', 'In the manuscripts, the readings ἀϊδὴς, αἰδώ and αἰδῶ are attested.');
INSERT INTO public.quotations VALUES (2132, 'Adversus haec ab Artemidoro illa dicuntur: non has tantum stellas quinque discurrere, sed has solas observatas esse;', '', 1889, 'Against such theories Artemidorus gives the following arguments: not these five planets only have erratic movements but these five are the only ones seen.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.255.xml', '7.13.1', 2213, '254-255', '');
INSERT INTO public.quotations VALUES (2221, 'ⲁⲕⲉⲓⲙⲉ ⲇⲉ ⲉⲧⲙⲣⲣⲉ ⲛⲧϭⲓⲙⲙⲟⲩⲧⲉ ⲁⲩⲱ ⲁⲕⲟⲩⲱⲛ ⲙⲡⲉⲕⲧⲟ ⲙⲡⲥⲟⲩⲛϩⲱⲣ', 'akeime de etmrre ntcimmoute auô akouôn mpekto mpsounhôr', 1962, 'And dost thou understand the band of Pleiades, and hast thou opened the barrier of Orion?', 'Job', 'religious text', '', '', '', 'https://data.copticscriptorium.org/texts/old-testament/18_job_38/', '38 31', 2295, '', '');
INSERT INTO public.quotations VALUES (1398, 'Sed tunc uxores dabuntur, cum post XXX annos Saturnus intrat in Cancrum', '', 1247, 'Wives will be given when Saturn enters Cancer after 30 years', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '5.3.8', 1490, '2.30', '');
INSERT INTO public.quotations VALUES (1575, 'basis nocte a fortuna ad genium.', '', 1398, '[The lot of] basis [can be calculated by adding to the ascendant the amount of degrees] at night from the lot of fortune until the lot of the daemon', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '6.32.56', 1667, '2.189', '');
INSERT INTO public.quotations VALUES (1755, 'XIII. vero et XIV. et XV. in humeris', '', 1562, 'the 13th, 14th and 15th degree however is in the shoulders [of Taurus]', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '8.4.2', 1847, '2.288', '');
INSERT INTO public.quotations VALUES (1774, 'XVIII. et XIX. et XX. in australis Gemini capite collocantur,', '', 1578, 'the degrees 18, 19 and 20 are located in the head of the southern twin', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '8.4.3', 1865, '2.289', '');
